INSERT INTO Palabra (nombre,significado) values ('Actor','Persona que interpreta un papel en una obra teatral, cinematográfica, radiofónica o televisiva.');
INSERT INTO Palabra (nombre,significado) values ('Embarazo','Estado de la mujer gestante.');
INSERT INTO Palabra (nombre,significado) values ('Reno','Mamífero cérvido de unos 90 cm de altura en la cruz, cuerpo robusto, pelaje espeso marrón o gris, cola muy corta, pezuñas gruesas, y astas ramificadas, más grandes en el macho; habita en la tundra y los bosques de las regiones frías del hemisferio norte; se domestica con facilidad y se emplea como animal de tiro de trineos.');
INSERT INTO Palabra (nombre,significado) values ('Rayo','Porción de cualquier forma de energía radiante que se propaga en línea recta.');
INSERT INTO Palabra (nombre,significado) values ('Tararear','Cantar o imitar una melodía sin articular bien las palabras, generalmente sustituyendo los sonidos por sílabas sin sentido, como ta, ra, la, etc.');
INSERT INTO Palabra (nombre,significado) values ('Fosil','Que se ha petrificado mediante procesos químicos y geológicos y se encuentra en los antiguos depósitos sedimentarios de la corteza terrestre.');
INSERT INTO Palabra (nombre,significado) values ('Deporte','Actividad o ejercicio físico, sujeto a determinadas normas, en que se hace prueba, con o sin competición, de habilidad, destreza o fuerza física.');
INSERT INTO Palabra (nombre,significado) values ('Buzo','Persona que tiene por oficio trabajar sumergida en el agua realizando actividades científicas, militares, de rescate, de pesca, etc., generalmente equipada con un traje especial y tubos de oxígeno para respirar.');
INSERT INTO Palabra (nombre,significado) values ('Escoba','Utensilio para barrer formado por un conjunto de ramas secas atadas en un extremo, o por un palo o caña que lleva sujeto en uno de sus extremos un cepillo grueso.');
INSERT INTO Palabra (nombre,significado) values ('Indumentaria','Nombre genérico de la ropa que cubre y resguarda el cuerpo humano.');
INSERT INTO Palabra (nombre,significado) values ('Declamar','Recitar o decir en voz alta un bello texto, dándole la entonación adecuada, en especial recitar un actor su papel en una obra.');
INSERT INTO Palabra (nombre,significado) values ('Bizco','Que está desviado de la dirección normal.');
INSERT INTO Palabra (nombre,significado) values ('Engaño','Acción o conjunto de palabras o acciones con que se engaña a alguien o se le hace creer algo que no es verdad.');
INSERT INTO Palabra (nombre,significado) values ('Pata','Pie o pierna de un animal.');
INSERT INTO Palabra (nombre,significado) values ('Triturar','Desmenuzar o moler una materia sólida en trozos pequeños sin llegar a convertirla en polvo.');
INSERT INTO Palabra (nombre,significado) values ('Cortar','Dividir una cosa en dos o más partes con un instrumento afilado.');
INSERT INTO Palabra (nombre,significado) values ('Sepultura','Cavidad excavada en la tierra, construcción u otro lugar en que se entierra uno o varios cadáveres.');
INSERT INTO Palabra (nombre,significado) values ('Farol','Utensilio para alumbrar que consiste en una caja con una o más caras de cristal o de otro material transparente que contiene una luz.');
INSERT INTO Palabra (nombre,significado) values ('Cascada','Corriente de agua que cae desde cierta altura a causa de un brusco desnivel en su cauce, especialmente en un río.');
INSERT INTO Palabra (nombre,significado) values ('Significado','Que es conocido, importante o reputado en algún ámbito.');
INSERT INTO Palabra (nombre,significado) values ('Tormenta','Tempestad de corta duración y especialmente violenta.');
INSERT INTO Palabra (nombre,significado) values ('Medico','Persona que tiene la autorización académica y legal para ejercer la medicina.');
INSERT INTO Palabra (nombre,significado) values ('Mirar','Dirigir la vista hacia algo y fijar la atención en ello.');
INSERT INTO Palabra (nombre,significado) values ('Pulgar','Dedo más grueso de la mano o del pie.');
INSERT INTO Palabra (nombre,significado) values ('Charco','Agua u otro líquido retenidos en un hoyo o cavidad de la tierra o sobre el suelo; es de dimensiones pequeñas, poca profundidad y se mantiene poco tiempo.');
INSERT INTO Palabra (nombre,significado) values ('Rojo','Color como el de la sangre, los tomates maduros o las amapolas; es el primer color del espectro solar.');